import React, { useState } from 'react';
import PhotoListDetail from './PhotoListDetail';
import { createUseStyles } from 'react-jss';

const maxWidth = 700;
const useStyles = createUseStyles({
	grid: {
		display: 'grid',
		gridTemplateColumns: 'repeat(4, 4fr)',
		gridTemplateRows: 'repeat(4, 30vw)',
		gridGap: '15px',
	},
	lightbox: {
		zIndex: '1',
		position: 'fixed',
		top: 0,
		left: 0,
		width: '100%',
		height: '100%',
		backgroundColor: 'rgba(255, 255, 255, 0.5)',
		display: 'flex',
		alignItems: 'center',
		justifyContent: 'space-between',
	},
	lightboxImg: {
		height: '80vh',
		maxWidth: '90vw',
		objectFit: 'cover',
	},
	[`@media (max-width: ${maxWidth}px)`]: {
		grid: {
			gridTemplateColumns: 'repeat(2, 4fr)',
		},
	},
});

const PhotoList = ({ images }) => {
	const classes = useStyles();
	const [imageToShow, setImageToShow] = useState('');
	const [lightboxDisplay, setLightBoxDisplay] = useState(false);

	const showImage = (image) => {
		setImageToShow(image);
		setLightBoxDisplay(true);
	};

	const hideLightBox = () => {
		setLightBoxDisplay(false);
	};

	//show next image in lightbox
	const showNext = (e) => {
		e.stopPropagation();
		let currentIndex = images.indexOf(imageToShow);
		if (currentIndex >= images.length - 1) {
			setLightBoxDisplay(false);
		} else {
			console.log(imageToShow);
			let nextImage = images[currentIndex + 1];
			setImageToShow(nextImage);
		}
	};

	//show previous image in lightbox
	const showPrev = (e) => {
		e.stopPropagation();
		let currentIndex = images.indexOf(imageToShow);
		if (currentIndex <= 0) {
			setLightBoxDisplay(false);
		} else {
			let nextImage = images[currentIndex - 1];
			console.log(nextImage);
			setImageToShow(nextImage);
		}
	};

	return (
		<div className={classes.grid}>
			{images.length > 0
				? images.map((image) => (
						<img
							key={image.id}
							onClick={() => {
								showImage(image.urls.regular);
							}}
							src={image.urls.regular}
							alt={image.description}
						/>
						/* <PhotoListDetail
							key={image.id}
							urls={image.urls.regular}
							description={image.description}
							showImage={showImage}
						/> */
				  ))
				: []}
			{lightboxDisplay ? (
				<div className={classes.lightbox} onClick={hideLightBox}>
					<button onClick={showPrev}>тна</button>
					<img className={classes.lightboxImg} src={imageToShow} alt="xxx"></img>
					<button onClick={showNext}>тнв</button>
				</div>
			) : (
				''
			)}
		</div>
	);
};

export default PhotoList;
