import React from 'react';
import { createUseStyles } from 'react-jss';

const useStyles = createUseStyles({
	lightbox: {
		zIndex: '1',
		position: 'fixed',
		top: 0,
		left: 0,
		width: '100%',
		height: '100%',
		backgroundColor: 'rgba(255, 255, 255, 0.5)',
	},
	lightboxImg: {
		position: 'relative',
		display: 'grid',
		flexDirection: 'column',
		justifyContent: 'center',
		margin: 'auto',
		padding: '0 0 0 0',
		height: '80vh',
		maxWidth: '90vw',
		/* height: '80vh',
		maxWidth: '90vw',
		objectFit: 'cover', */
	},
	nextBtn: {
		cursor: 'pointer',
		width: 'auto',
		backgroundColor: 'Transparent',
		border: 'none',
		padding: '3em',
		textAlign: 'center',
		margin: '4px',
		opacity: '0.6',
		transition: '0.3s',
		outline: 'none',
	},
	prevBtn: {
		cursor: 'pointer',
		width: 'auto',
		backgroundColor: 'Transparent',
		backgroundRepeat: 'no-repeat',
		overflow: 'hidden',
		border: 'none',
		padding: '3em',
		textAlign: 'center',
		margin: '4px',
		opacity: '0.6',
		transition: '0.3s',
		outline: 'none',
		position: 'relative',
		top: '50%',
		right: '10%',
	},
	nextArrow: {
		border: 'solid black',
		borderWidth: '0 4px 4px 0',
		display: 'inline-block',
		padding: '10px',
		transform: 'rotate(135deg)',
		webkitTransform: 'rotate(135deg)',
	},
	prevArrow: {
		border: 'solid black',
		borderWidth: '0 4px 4px 0',
		display: 'inline-block',
		padding: '10px',
		transform: 'rotate(-45deg)',
		webkitTransform: 'rotate(-45deg)',
	},
});

const LightBox = ({ imageToShow, setImageToShow, hideLightBox, images }) => {
	const classes = useStyles();

	const showNextImage = (e) => {
		e.stopPropagation();
		let currentIndex = images.indexOf(imageToShow);
		if (currentIndex >= images.length - 1) {
			setImageToShow(images[0]);
		} else {
			let nextImage = images[currentIndex + 1];
			setImageToShow(nextImage);
		}
	};

	const showPrevImage = (e) => {
		e.stopPropagation();
		let currentIndex = images.indexOf(imageToShow);
		if (currentIndex <= 0) {
			setImageToShow(images[images.length - 1]);
		} else {
			let nextImage = images[currentIndex - 1];
			setImageToShow(nextImage);
		}
	};

	return (
		<div className={classes.lightbox} onClick={hideLightBox}>
			<button className={classes.nextBtn} onClick={showPrevImage}>
				<i className={classes.nextArrow}></i>
			</button>
			<img className={classes.lightboxImg} src={imageToShow.urls.regular} alt={imageToShow.description}></img>
			<button className={classes.prevBtn} onClick={showNextImage}>
				<i className={classes.prevArrow}></i>
			</button>
		</div>
	);
};

export default LightBox;
