import React from 'react';
import { CapitalizeFirstLetter } from '../utilities/CapitalizeFirstLetter';
import dayjs from 'dayjs';
import { createUseStyles } from 'react-jss';

const useStyles = createUseStyles({
	overlay: {
		position: 'absolute',
		bottom: '10%',
		background: 'rgba(0, 0, 0, 0.5)',
		width: '20%',
		color: 'white',
		transition: '.5s ease',
		opacity: 1,
		fontSize: '1rem',
		padding: '1em',
		textAlign: 'bottom',
		'&:hover': {
			opacity: 1,
		},
	},
	content: {
		display: 'flex',
		flexDirection: 'column',
		flexWrap: 'wrap',
		alignItems: 'center',
		justifyContent: 'space-evenly',
	},
});

const Overlay = ({ imageMetaData }) => {
	const classes = useStyles();
	const year = dayjs(imageMetaData.created_at).format('YYYY');
	return (
		<div className={classes.overlay}>
			<section className={classes.content}>
				<h4 className={classes.name}>{imageMetaData.user.name}</h4>
				<span>
					{imageMetaData.user.location
						? CapitalizeFirstLetter(imageMetaData.user.location)
						: 'No location found'}
				</span>
				<span>{year}</span>
			</section>
		</div>
	);
};

export default Overlay;
