import React, { useState } from 'react';
import { CapitalizeFirstLetter } from '../utilities/CapitalizeFirstLetter';
import dayjs from 'dayjs';
import { createUseStyles } from 'react-jss';

const useStyles = createUseStyles({
	overlay: {
		position: 'absolute',
		bottom: '10%',
		/* background: 'rgba(0, 0, 0, 0.5)', */
		width: '20%',
		color: 'white',
		transition: '.5s ease',
		fontSize: '1rem',
		padding: '1em',
		textAlign: 'bottom',
		/* '&:hover': {
			opacity: 1,
		}, */
	},
	content: {
		display: 'flex',
		flexDirection: 'column',
		/* flexWrap: 'wrap', */
		alignItems: 'center',
		/* justifyContent: 'space-evenly', */
	},
});

const Overlay = ({ imageMetaData }) => {
	const [metaData, setMetaData] = useState(false);
	console.log(metaData);
	function showMetaData() {

		if (!metaData) {
			setMetaData(true);
		} else {
			setMetaData(false);
		}
	}



	const classes = useStyles();
	const year = dayjs(imageMetaData.created_at).format('YYYY');
	return (
		<div>
			<button onClick={showMetaData}>+</button>
			<div className={classes.overlay}>
				{metaData ? <section className={classes.content}>
					<span>{imageMetaData.user.name}</span>
					<span>
						{imageMetaData.user.location
							? CapitalizeFirstLetter(imageMetaData.user.location)
							: 'No location found'}
					</span>
					<span>{year}</span>
				</section> : ''}
			</div>
		</div>
	);
};

export default Overlay;
